import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { selectUser } from "../features/userSlice";
import db from "../firebase";
import "./PlansScreen.css";
import { loadStripe } from "@stripe/stripe-js";

function PlansScreen() {
  const [products, setProducts] = useState([]);
  const user = useSelector(selectUser);
  const [subscription, setSubscription] = useState(null);

  useEffect(() => {
    db.collection("customers")
      .doc(user.uid)
      .collection("subscriptions")
      .get()
      .then((querySnapshot) => {
        querySnapshot.forEach(async (subscription) => {
          setSubscription({
            role: subscription.data().role,
            current_period_end: subscription.data().current_period_end.seconds,
            current_period_start: subscription.data().current_period_start
              .seconds,
          });
        });
      });
  }, [user.uid]);

  useEffect(() => {
    db.collection("products")
      .where("active", "==", true)
      .get()
      .then((querySnapshot) => {
        const products = {};
        querySnapshot.forEach(async (productDoc) => {
          products[productDoc.id] = productDoc.data();
          const priceSnap = await productDoc.ref.collection("prices").get();
          priceSnap.docs.forEach((price) => {
            products[productDoc.id].prices = {
              priceId: price.id,
              priceData: price.data(),
            };
          });
        });
        setProducts(products);
      });
  }, []);
  // console.log(products);
  console.log(subscription);
  const loadCheckout = async (priceId) => {
    const docRef = await db
      .collection("customers")
      .doc(user.uid)
      .collection("checkout_sessions")
      .add({
        price: priceId,
        success_url: window.location.origin,
        cancel_url: window.location.origin,
      });
    docRef.onSnapshot(async (snap) => {
      const { error, sessionId } = snap.data();
      if (error) {
        //Show an error to you customer and inspect your Cloud function logs in the Firebase console
        alert(`An error occured: ${error.message}`);
      }
      if (sessionId) {
        // We have a session, lets redirect to Checkout,
        //Initialize Stripe
        const stripe = await loadStripe(
          "pk_test_51HQzccJi6cwzy1sivjZse62bFU6ZV4ublhFLH6aMIueBQokwITmhy8NggRSCxyJqXaqwwZ0rbFK4kulwL72Cz8Dl00bbC8fabU"
        );
        stripe.redirectToCheckout({ sessionId });
      }
    });
  };
  return (
    <div className="plansScreen">
      {Object.entries(products).map(([productId, productData]) => {
        //add some logic to check if users subscription is active
        const isCurrentPackage = productData.name.includes(subscription?.role);
        //alternative code
        // const isCurrentPackage = productData.name?.toLowerCase().includes(subscription?.role);
        return (
          <div key={productId} className={`plansScreen__plan`}>
            <div className="planScreen__info">
              <h5>{productData.name}</h5>
              <h5>{productData.description}</h5>
            </div>
            <button onClick={() => loadCheckout(productData.prices.priceId)}>
              {isCurrentPackage ? "Current Package" : "Subscribe"}
            </button>
          </div>
        );
      })}
    </div>
  );
}

export default PlansScreen;
